function validateCheckboxChecked(form) {
    var isValid = true;
    var focusField = null;
    var i = 0;
    var fields = new Array();
    oRequired = new checkboxChecked();
    for (x in oRequired) {
       var field = form[oRequired[x][0]];

       if (field.type == 'checkbox') {
            if (field.checked != true) {

               if (i == 0) {
                   focusField = field;
               }
               fields[i++] = oRequired[x][1];
               isValid = false;
            }
        }
    }
    if (fields.length > 0) {
       focusField.focus();
       alert(fields.join('\n'));
    }
    return isValid;
}

// Trim whitespace from left and right sides of s.
function trim(s) {
    return s.replace( /^\s*/, "" ).replace( /\s*$/, "" );
}


            function validateMaxCheckboxesChecked(form) {
                var isValid = true;
                var focusField = null;
                var i = 0;
                var fields = new Array();
                oMaxLength = new maxCheckboxesChecked();

                var numChecked = 0;

                for (x in oMaxLength) {

                    var aCheckbox=document.getElementsByName(oMaxLength[x][0]);
                    if (aCheckbox != null) {
	                  for (var z=0; z<aCheckbox.length; z++) {
					  	if (aCheckbox[z].checked == true) {
					  		numChecked++;
					  	}
					  }
				  	}

                    var iMax = parseInt(parseFloat(oMaxLength[x][2]("max")));

                    if (numChecked > iMax) {
                        fields[i++] = oMaxLength[x][1]; // argumento para o mensagem do erro
                        isValid = false;
                    }
                }
                if (fields.length > 0) {
                   alert(fields.join('\n'));
                }
                return isValid;
            }


function validateMaskIfEnabled(form) {
    var isValid = true;
    var focusField = null;
    var i = 0;
    var fields = new Array();
    oMasked = new maskIfEnabled();
    for (x in oMasked) {

        var field = form[oMasked[x][0]];

        if ((field.type == 'text' || 
             field.type == 'textarea' ||
             field.type == 'password') && 
             (field.value.length > 0) && (field.disabled==false)) {

            if (!matchPattern(field.value, oMasked[x][2]("mask"))) {
                if (i == 0) {
                    focusField = field;
                }
                fields[i++] = oMasked[x][1];
                isValid = false;
            }
        }
    }

    if (fields.length > 0) {
       focusField.focus();
       alert(fields.join('\n'));
    }
    return isValid;
}

function matchPattern(value, mask) {
   return mask.exec(value);
}



function currencyMask(fld, milSep, decSep, e, maxLength, qtdCasas) {

	var backspace = 8; 

    var del = 46;

    var espaco = 32;

    var enter = 13;

    var sep = 0;

    var key = '';

    var i = 0;

    var j = 0;

    var len = 0;

    var len2 = 0;

    var strCheck = '0123456789';

    var aux = '';

    var aux2 = '';

    var whichCode = (window.Event) ? e.which : e.keyCode;

    if (whichCode == enter || whichCode == espaco) return false;

    if (whichCode == backspace || whichCode == del) return true;

    if (fld.value.length >= maxLength) return false;


    key = String.fromCharCode(whichCode);


    if (strCheck.indexOf(key) == -1) return false;


    len = fld.value.length;


    for(i = 0; i < len; i++)

        if ((fld.value.charAt(i) != '0') && (fld.value.charAt(i) != decSep)) break;

    aux = '';

    for(; i < len; i++)

        if (strCheck.indexOf(fld.value.charAt(i))!=-1) aux += fld.value.charAt(i);

    aux += key;

    len = aux.length;

    if (len == 0) fld.value = '';

    if (len == 1) fld.value = '0' + decSep + '0' + aux; 

    if (len == 2) fld.value = '0' + decSep + aux; 

    if (len > 2) { 

        aux2 = '';

        for (j = 0, i = len - 3; i >= 0; i--) {

            if (j == 3) {

                aux2 += milSep;

                j = 0;

            }

            aux2 += aux.charAt(i);

            j++;

        }        

        fld.value = '';

        len2 = aux2.length;

        for (i = len2 - 1; i >= 0; i--) 

            fld.value += aux2.charAt(i);

        fld.value += decSep + aux.substr(len - qtdCasas, len);

    }

    return false;
}



function mascaraPercentual5Casas(objTextBox, SeparadorMilesimo, SeparadorDecimal, e) {
	var sep = 0;
	var key = '';
	var i = j = 0;
	var len = len2 = 0;
	var strCheck = '0123456789';
	var aux = aux2 = '';

	var whichCode = (window.Event) ? e.which : e.keyCode;
	if (whichCode == 13 || whichCode == 8)
		return true;

	key = String.fromCharCode(whichCode); // Valor para o c칩digo da Chave
	if (strCheck.indexOf(key) == -1)
		return false; // Chave inv치lida
	len = objTextBox.value.length;
	//alert(len);

	for (i = 0; i < len; i++)
		if ((objTextBox.value.charAt(i) != '0')
				&& (objTextBox.value.charAt(i) != SeparadorDecimal))
			break;
	aux = '';

	for (; i < len; i++)
		if (strCheck.indexOf(objTextBox.value.charAt(i)) != -1)
			aux += objTextBox.value.charAt(i);
	aux += key;
	len = aux.length;
	 //alert(len);
	if (len == 9) {
		return false;
	}
	if (len == 0)
		objTextBox.value = '';
	if (len == 1)
		objTextBox.value = '0' + SeparadorDecimal + '0' + '0' + '0' + '0' + aux;
	if (len == 2)
		objTextBox.value = '0' + SeparadorDecimal + '0' + '0' + '0' + aux;
	if (len == 3)
		objTextBox.value = '0' + SeparadorDecimal + '0' + '0' + aux;
	if (len == 4)
		objTextBox.value = '0' + SeparadorDecimal + '0' + aux;
	if (len == 5)
		objTextBox.value = '0' + SeparadorDecimal + aux;
	if (len > 5) {
		aux2 = '';
		//alert(i);
		//alert(len);
		for (j = 0, i = len - 6; i >= 0; i--) {
			if (j == 3) {
				aux2 += SeparadorMilesimo;
				j = 0;
			}
			aux2 += aux.charAt(i);
			//alert(aux.charAt(i));
			//alert(i);
			//alert(aux2);
			j++;
		}
		objTextBox.value = '';
		//alert('1');
		len2 = aux2.length;
		//alert('2');
		for (i = len2 - 1; i >= 0; i--)
			objTextBox.value += aux2.charAt(i);
		objTextBox.value += SeparadorDecimal + aux.substr(len - 5, len);
		//alert(aux.substr(len - 5, len));
	}
		return false;
}



